// src/node_modules/@sudoku/algorithms/types.ts

/**
 * 数独盘面类型：9x9 的二维数组
 * - 空格用 0 表示
 * - 已填写数字为 1~9
 */
export type Grid = number[][];

/**
 * 单元格坐标，格式为 [x, y]
 * - x: 列索引，范围 0~8
 * - y: 行索引，范围 0~8
 */
export type CellPos = [number, number];

/**
 * 提示结果对象
 */
export interface HintResult {
  /**
   * 提示策略类型（人类可读）
   */
  hintType: string;

  /**
   * 目标单元格坐标 [x, y]
   */
  targetCell: CellPos;

  /**
   * 应填入的数字（1~9）
   */
  value: number;

  /**
   * 与该提示相关的其他格子坐标列表
   */
  relatedCells: CellPos[];

  /**
   * 人类可读的解释文本
   */
  reason: string;
}

/**
 * 策略接口：所有提示策略必须实现此接口
 */
export interface HintStrategy {
  findHint(grid: Grid): HintResult | null;
}