import { writable } from 'svelte/store';

export const guideState = writable({
  active: false,
  step: 0  // 0=无引导, 1=指引菜单按钮, 2=指引Enter Code
});

export const guideActions = {
  start() {
    guideState.set({ active: true, step: 1 });
  },
  
  next() {
    // 进入第二步：active保持true，step改为2
    guideState.set({ active: true, step: 2 });
  },
  
  complete() {
    guideState.set({ active: false, step: 0 });
  }
};